{
    "steps": [
        {
            "description": "Initialize workflow by reading current state and checking for new emails",
            "tasks": [
                {
                    "description": "Read metadata from data/metadata.json to get last_email_check timestamp and current processing statistics",
                    "agent": "file_manager"
                },
                {
                    "description": "Check Gmail for new emails received after the last_email_check timestamp and add them to the spreadsheet with proper metadata including is_latest flag management",
                    "agent": "gmail_manager"
                }
            ]
        },
        {
            "description": "Classify unprocessed emails and gather context for those requiring responses",
            "tasks": [
                {
                    "description": "Classify any emails in the spreadsheet that have empty 'type' fields by analyzing both sender domain and email content, then update with appropriate type and initial status",
                    "agent": "email_classifier"
                },
                {
                    "description": "For each email marked with action_required=1, gather full conversation history and load relevant context files to create comprehensive context packages",
                    "agent": "context_gatherer"
                }
            ]
        },
        {
            "description": "Generate and enhance email drafts for all emails requiring responses",
            "tasks": [
                {
                    "description": "Generate appropriate email response drafts based on the context packages, considering email type, status, and conversation history, and suggest status updates",
                    "agent": "draft_generator"
                },
                {
                    "description": "Enhance the generated drafts by applying grammar improvements, ensuring brand consistency, adding signature from enhancer_context.txt, and optimizing tone for recipient type",
                    "agent": "draft_enhancer"
                }
            ]
        },
        {
            "description": "Execute email workflow completion by saving drafts and updating records",
            "tasks": [
                {
                    "description": "Save enhanced email drafts to Gmail as draft messages and update the spreadsheet with action_required=0, new status values, processing notes, and follow-up dates",
                    "agent": "spreadsheet_manager"
                },
                {
                    "description": "Update metadata.json with current timestamp for last_email_check, increment processing counters, and save the updated state",
                    "agent": "file_manager"
                }
            ]
        }
    ],
    "is_complete": false
}
